import React ,{ useEffect }from 'react';
import logo from './logo.svg';
import './App.css';

function App() {
  //顏色字串
  let colorStr="";

  function color(){ 
      const r = function () { return Math.floor(Math.random()*256) };
      return colorStr="rgb(" + r() +"," + r() +"," + r() +")";
  }

//ul
  for(let i=1;i<9;i++){ 
      const ul=document.createElement("ul");
      document.body.appendChild(ul);           
  }
//li 
  for(let j=0;j<8;j++){

      var x = document.querySelectorAll("ul");
      for(let k=0;k<x.length;k++){
          let li = document.createElement("li");
          color();
          document.querySelectorAll("ul")[k].appendChild(li);
          document.querySelectorAll("ul")[k].appendChild(li).style.backgroundColor=colorStr;
      }

  }

//click 
  document.addEventListener("click", function(event){
      var targetElement = event.srcElement.localName;

      if(targetElement === 'li'){
          color();
          event.srcElement.style.backgroundColor=colorStr;
      }
  })

  return (
    <div className="App">
