import React, { useState} from "react"
import RadomColor from "./RadomColor"
import './App.css'
import './Title.css'

function App() {
  const [isHomePage,setIsHomePage] = useState(true)
  const [isRadomPage,setIsRadomPage] = useState(false)

  const Height = window.screen.height
  const titleHeight = document.getElementById('title').style.height
  document.getElementById('contain').style.height = (Height - titleHeight) + 'px'
  const home = () => {
    setIsHomePage(true)
    setIsRadomPage(false)
  }
  const radomColor = () => {
    setIsRadomPage(true)
    setIsHomePage(false)
  }

  return (
    <div id='main'>
        <div id='title'>
            <h1>功能選單</h1>
            <p>
                <a onClick={home}>首頁</a>
                <a onClick={radomColor}>隨機顏色</a>
            </p>
        </div>
        <div id='contain'>
          <p style={{display: isHomePage ? 'block' : 'none' }}>歡迎小遊戲！</p>
          <RadomColor
            isRadomPage={isRadomPage}
          />
        </div>
    </div>
  )
}

export default App;
