import React, { useState} from "react"
import { HashRouter, Route, Link } from "react-router-dom"
import Title from "./Title"
import RadomColor from "./RadomColor"
// import SameColor from "./SameColor"
import './App.css'
import './Title.css'

function App() {
  const [isHomePage,setIsHomePage] = useState(true)
  const [isRadomPage,setIsRadomPage] = useState(false)

  const home = () => {
    setIsHomePage(isHomePage)

  }
  const radomColor = () => {
    setIsRadomPage(!isRadomPage)
  }

  return (
    <HashRouter>
        <div id='title'>
            <h1>功能選單</h1>
            <ul>
                <li onClick={home}>首頁</li>
                <li onClick={radomColor}>隨機顏色</li>
            </ul>
        </div>
        <p style={{display: isHomePage ? 'block' : 'none' }}>歡迎小遊戲！</p>
        <div style={{display: isHomePage ? 'block' : 'none' }}>Test</div>
            <Route exact path="/radomColor" component={RadomColor} />

    </HashRouter>
  )
}

export default App;
