import React, { useState, useEffect} from "react"
import RadomColor from "./RadomColor"
import './App.css'
import './Title.css'

function App() {
  const [isSamePage,setIsSamePage] = useState(false)
  const [isRadomPage,setIsRadomPage] = useState(true)
  const [viewHeight,setViewHeight] = useState(0)

  const Height = window.screen.height
  // const init = () => {
  //   setViewHeight(document.getElementById('contain').style.height = (Height * 0.7) - 50 + 'px')
  // }
  const sameColor = () => {
    setIsSamePage(true)
    setIsRadomPage(false)
  }
  const radomColor = () => {
    setIsRadomPage(true)
    setIsSamePage(false)
    setViewHeight(document.getElementById('contain').style.height = (Height * 0.8) - 50 + 'px')
  }
  // useEffect(() =>{
  //   init()
  // },[])
  return (
    <div id='main'>
        <div id='title'>
            <h1>功能選單</h1>
            <p>
                <a onClick={radomColor}>隨機顏色</a>
                <a onClick={sameColor}>相同顏色</a>
            </p>
        </div>
        <div id='contain'>
          <p style={{display: isSamePage ? 'block' : 'none' }}>歡迎小遊戲！</p>
          <RadomColor
            viewHeight={viewHeight}
            isRadomPage={isRadomPage}
          />
        </div>
    </div>
  )
}

export default App;
