import React, { useState , useEffect} from 'react'
import * as R from 'ramda'

function RadomColor({ isRadomPage }){
      const hasData = R.compose(
        R.not,
        R.either(R.isNil, R.isEmpty)
      ) 

      const BOX_NUMBER = 2


      // const [color,setColor] = useState('rgb(2,5,8)')
      function fetchColor () { 
          const r = () =>  Math.floor(Math.random()*256)
          // return setColor(`rgb(${r()},${r()},${r()})`)
        return `rgb(${r()},${r()},${r()})`
      }

      // let box =[
      //   {
      //     'id': 0,
      //     'color': 'rgb(255, 5, 255)'
      //   },
      //   {
      //     'id': 1,
      //     'color': 'rgb(255, 255, 5)'
      //   }
      // ]
     let box = []
      const [data,setData] = useState(box)
      console.log(data)
      for(let i = 0; i < BOX_NUMBER; i ++) {
        // fetchColor()
        const item = {}
        item['id'] = i
        item['color'] = fetchColor()
        box.push(item)
      }
      console.log('box', box)

      const changeColor = id => {
        const item = {
          'id': id,
          'color': fetchColor()
        }

        const oldBox =  box.filter(item =>item.id !== id)

        const newData = oldBox.concat(item)
        setData(newData)
        console.log(newData,oldBox,item)

      }

      // useEffect(() => {

      //   fetchColor()

      // },[])
      return (
          <div className='RadomColor' style={{display: isRadomPage ? 'flex' : 'none'}}>
            {hasData(data) && data.map(item => (
                <div
                  className = "radomDiv"
                  key = {item.id}
                  id = {item.id}
                  style = {{backgroundColor: item.color }}
                  onClick = {() => {
                    changeColor(item.id)
                  }}
                />
            ))}
          </div>

      )
}

export default RadomColor
