import React, { useState , useEffect } from 'react'
import * as R from 'ramda'

function RadomColor({ isRadomPage }){
  const hasData = R.compose(
    R.not,
    R.either(R.isNil, R.isEmpty)
  )

  const BOX_NUMBER = 2
  let box = []
  const [data,setData] = useState(box)

  function fetchColor () { 
    const r = () => Math.floor(Math.random()*256)
    return `rgb(${r()},${r()},${r()})`
  }

  const init = () => {

      for(let i = 0; i < BOX_NUMBER; i ++) {
        const item = {
          id: i,
          color: fetchColor()
        }
        box.push(item)
      }
      setData(box)
   console.log(data)
  }

  useEffect(() => {
    init()
    setData(box)
  },[init,box])

  const changeColor = id => {
    const item = {
      'id': id,
      'color': fetchColor()
    }
    const oldBox = box.filter(item =>item.id !== id)

    const newData = oldBox.concat(item)
    setData(newData)
    console.log(newData,oldBox,item)
  }
  console.log('box', box)
  console.log('data', data)
  return (
      <div className = 'RadomColor' style = {{display: isRadomPage ? 'flex' : 'none'}}>
        {hasData(data) && data.map(item => (
          <div
            className = "radomDiv"
            key = {item.id}
            id = {item.id}
            style = {{backgroundColor: item.color }}
            onClick = {() => {
              changeColor(item.id)
            }}
          />
        ))}
      </div>

  )
}

export default RadomColor
