import React, { useState , useEffect} from 'react'
import * as R from 'ramda'

function RadomColor({ isRadomPage }){
      const hasData = R.compose(
        R.not,
        R.either(R.isNil, R.isEmpty)
      ) 

      const BOX_NUMBER = 2


      // const [color,setColor] = useState('rgb(2,5,8)')

      function fetchColor () { 
          const r = function () { return Math.floor(Math.random()*256) }
          return setColor(`rgb(${r()},${r()},${r()})`)
      }

      let box =[
        {
          'id': 0,
          'color': 'rgb(255, 5, 255)'
        },
        {
          'id': 1,
          'color': 'rgb(255, 255, 5)'
        }
      ]

      const [data,setData] = useState(box)
      // for(let i = 0; i < BOX_NUMBER; i ++) {
      //   fetchColor()
      //   const item = {}
      //   item['id'] = i
      //   item['color'] = color
      //   box.push(item)
      // }

      const changeColor = id => {
        fetchColor()
        const item = {
          'id': id,
          'color': color
        }

        box.splice(id, 1, item)
        setData(box)
        console.log('click',id)

      }

      useEffect(() => {

        fetchColor()

      },[])
      return (
          <div className='RadomColor' style={{display: isRadomPage ? 'flex' : 'none'}}>
            {hasData(data) && data.map(item => (
                <div
                  className = "radomDiv"
                  key = {item.id}
                  id = {item.id}
                  style = {{backgroundColor: item.color }}
                  onClick = {() => {
                    changeColor(item.id)
                  }}
                />
            ))}
          </div>

      )
}

export default RadomColor
