import React ,{ useEffect }from 'react'

function SameColor({ isSamePage }){
    const init = () => {
        //顏色字串
        let colorStr='';
        function color () { 
            const r = function () { return Math.floor(Math.random()*256) };
            return colorStr = 'rgb(' + r() +',' + r() +',' + r() +')';
        }
        const Height = window.screen.height * 0.8
        const Width = window.screen.width
        const ulHeight = (Height - 110 )/8
        const ulWidth = Width * 0.9
        const liWidth = (ulWidth * 0.9)/8
        //ul
          for (let i=1;i<9;i++) { 
              const ul = document.createElement('ul');
              document.querySelector('.SameColor').appendChild(ul).style.width = ulWidth + 'px'      
          }
        //li 
          for (let j=0;j<8;j++) {
              const x = document.querySelectorAll('ul');
              for (let k=0;k<x.length;k++) {
                  let li = document.createElement('li');
                  // color();
                  document.querySelectorAll('ul')[k].appendChild(li).style.height = ulHeight + 'px' 
                  document.querySelectorAll('ul')[k].appendChild(li).style.width = liWidth + 'px' 
                  // document.querySelectorAll('ul')[k].appendChild(li).style.backgroundColor=colorStr;
              }
          }
        //color Array
        let colorArray =[]
        const liNumber = document.querySelectorAll('li').length; 
        for (let l=0;l<liNumber;l++) {
          color();
          colorArray.push(colorStr)
        }
        console.log('colorArray',colorArray)
        //fetch li
        function sameColor () {
          let liArray = []
          for (let m=0;m<liNumber;m++) {
            liArray.push(m)
          }
          console.log('liArray',liArray)
          const num = function () { return Math.floor(Math.random()*65) };
          for (let n=1;n=liNumber;n++){
            console.log(num)
          }

        }
        //click 
          document.addEventListener ('click', function(event) {
              var targetElement = event.srcElement.localName;

              if (targetElement === 'li') {
                  color();
                  event.srcElement.style.backgroundColor=colorStr;
              }
          })
      }

      useEffect(()=>{
        init()
      },[])
      return (
        <div className='SameColor' style={{display: isSamePage ? 'block' : 'none'}}></div>
      );
}

export default SameColor;
