    }

    const oldBox = box.filter(item =>item.id !== id)

    const newData = oldBox.concat(item)
    setData(newData)
    console.log(newData,oldBox,item)

  }

  useEffect(() => {
    function fetchColor () { 
      const r = () => Math.floor(Math.random()*256)
    return `rgb(${r()},${r()},${r()})`
  }
  let box = []
  const [data,setData] = useState(box)
  console.log(data)
  for(let i = 0; i < BOX_NUMBER; i ++) {
    // fetchColor()
    const item = {
      id: i,
      color: fetchColor()
    }
    // item['id'] = i
    // item['color'] = fetchColor()
    box.push(item)
  }

  },[])
  return (
      <div className='RadomColor' style={{display: isRadomPage ? 'flex' : 'none'}}>
        {hasData(data) && data.map(item => (
          <div
            className = "radomDiv"
            key = {item.id}
            id = {item.id}
            style = {{backgroundColor: item.color }}
            onClick = {() => {
              changeColor(item.id)
            }}
          />
        ))}
      </div>

  )
}

export default RadomColor
